import React, { useState } from 'react';
import './App.css';
import LineInCircle from './components/Radar/LineInCircle/LineInCircle';
import Radar from './components/Radar/Radar';
import RulerRightLeft from './components/Ruler/RulerRightLeft';
import RulerTopBottom from './components/Ruler/RulerTopBottom';

function App() {
  const [showImage, setShowImage] = useState(false); // Добавляем состояние

  const handleButtonClick = () => {
    setShowImage((prev) => !prev); // Переключаем состояние
  };

  return (
    <div className="App">
      <nav>
        <main>
          <LineInCircle />
          <div className="square">
            <RulerTopBottom />
            <Radar showImage={showImage} /> {/* Передаем состояние в компонент Radar */}
            <RulerRightLeft />
          </div>
        </main>
        <aside>
          <button className='proba' onClick={handleButtonClick}>
            Показать/Скрыть картинку
          </button>
        </aside>

      </nav>
    </div>
  );
}

export default App;
