import React, { useState } from 'react';
import './App.css';

import Radar from './components/Radar/Radar';
import RulerRightLeft from './components/Ruler/RulerRightLeft';
import RulerTopBottom from './components/Ruler/RulerTopBottom';
import box from './Photo/eebc4b7dc05611ef8ab15ec00c958ca6_1.jpg';

let way = ""
let edinica = ' '
let object = ' '

function App() {
  const [showImage, setShowImage] = useState(false);
  const [isRed, setIsRed] = useState(false);

  const handleButtonClick = () => {
    setShowImage(true);
  };

  const handleColorChange = () => {
    setIsRed(true);
    setTimeout(() => {
      setIsRed(false);
    }, 2000);
  };

  return (
    <div className="App">
      <nav>
        <main>
          <div className="square">
            <RulerTopBottom />
            <Radar showImage={showImage} imageSrc={box} isRed={isRed} />
            <RulerRightLeft />
          </div>
        </main>
        <aside>

          {/* <button
            onClick={handleButtonClick}
          >
            Показать картинку
          </button> */}

          <div className="aside-container">
            <h2>Информация</h2>

            <div className="distance-info">
              <p>Тип объекта:</p>
              <div className="info-box">
                <p>{object}</p>
              </div>
            </div>

            <div className="distance-info">
              <p>Расстояние до цели:</p>
              <div className="info-box">
                <p>{way}{edinica}</p>
              </div>
            </div>

            <div className="distance-info">
              <p>Скорость объекта:</p>
              <div className="info-box">
                <p>{way}{edinica}</p>
              </div>
            </div>

            <div className="distance-info">
              <p>Время подлёта:</p>
              <div className="info-box">
                <p>{way}{edinica}</p>
              </div>
            </div>

            <div className="distance-info">
              <p>Сектор обнаружения:</p>
              <div className="info-box">
                <p>{way}{edinica}</p>
              </div>
            </div>

            <button
              onClick={handleColorChange}
              style={{
                marginTop: '20px',
                padding: '10px 20px',
                background: 'rgba(0, 255, 0, 0.2)',
                border: '1px solid rgba(0, 255, 0, 0.5)',
                color: '#00ff00',
                cursor: 'pointer',
                borderRadius: '4px',
                transition: 'all 0.3s ease',
                width: '100%'
              }}
            >
              Изменить цвет радара
            </button>
          </div>
        </aside>
      </nav>
    </div>
  );
}

export default App;
