import React, { useState } from 'react';
import './App.css';
import Radar from './components/Radar/Radar';

function App() {
  const [showImage, setShowImage] = useState(false); // Состояние

  // Обработчик клика
  const handleButtonClick = () => {
    setShowImage((prev) => !prev); // Переключаем состояние
  };

  return (
    <div className="App">
      <nav>
        <main>
          <div className="square">
            <Radar showImage={showImage} /> {/* Передаем состояние в Radar */}
          </div>
        </main>
        <aside>
          <button className="proba" onClick={handleButtonClick}>
            {showImage ? 'Скрыть картинку' : 'Показать картинку'}
          </button>
        </aside>
      </nav>
    </div>
  );
}

export default App;
