import classes from './Radar.module.css';

const Radar = () => {
    return (
        <div className={classes.radarContainer}>
            <div className={classes.radar}>
                {/* Внутренние круги */}
                <div className={classes.innerCircle}></div>
                <div className={classes.innerCircle2}></div>
                <div className={classes.innerCircle3}></div>
                
                {/* Линии на углах 360°-180°, 90°-270°, и перекрестные линии */}
                <div className={`${classes.line} ${classes.line1}`}></div> {/* 360° to 180° */}
                <div className={`${classes.line} ${classes.line2}`}></div> {/* 90° to 270° */}
                <div className={`${classes.line} ${classes.line45}`}></div> {/* 45° */}
                <div className={`${classes.line} ${classes.line135}`}></div> {/* 135° */}
                <div className={`${classes.line} ${classes.line225}`}></div> {/* 225° */}
                <div className={`${classes.line} ${classes.line315}`}></div> {/* 315° */}
            </div>

            {/* Добавление вращающегося треугольника */}
            <div className={classes.triangle}></div>

            <div className={classes.degreeLabelTop}>360</div>
            <div className={classes.degreeLabelRight}>90</div>
            <div className={classes.degreeLabelBottom}>180</div>
            <div className={classes.degreeLabelLeft}>270</div>

            <div className={classes.degreeMarkings}>
                {Array.from({ length: 36 }).map((_, index) => (
                    <div
                        key={index}
                        className={`${classes.degreeMarking} ${classes[`rotate-${index * 10}`]}`}
                    ></div>
                ))}
            </div>
        </div>
    );
};

export default Radar;
