import React, { useState } from 'react';
import Gradus from './LineInCircle/Gradus/Gradus';
import classes from './Radar.module.css';
import LineInCircle from './LineInCircle/LineInCircle';

const Radar = ({ showImage, imageSrc }) => {
    const [activeSector, setActiveSector] = useState(null);

    const handleButtonClick = (sector) => {
        setActiveSector(sector);
    };

    const sectors = [
        { id: 1, startAngle: 0, endAngle: 45 },
        { id: 2, startAngle: 45, endAngle: 90 },
        { id: 3, startAngle: 90, endAngle: 135 },
        { id: 4, startAngle: 135, endAngle: 180 },
        { id: 5, startAngle: 180, endAngle: 225 },
        { id: 6, startAngle: 225, endAngle: 270 },
        { id: 7, startAngle: 270, endAngle: 315 },
        { id: 8, startAngle: 315, endAngle: 360 }
    ];

    return (
        <div className={classes.radarContainer}>
            <LineInCircle />
            <div className={classes.radar}>
                {/* Внутренние круги */}
                <div className={classes.innerCircle}></div>
                <div className={classes.innerCircle2}></div>
                <div className={classes.innerCircle3}></div>

                {/* Сектора */}
                {sectors.map(sector => (
                    <div
                        key={sector.id}
                        className={`${classes.sector} ${activeSector === sector.id ? classes.active : ''}`}
                        style={{
                            transform: `rotate(${sector.startAngle}deg)`,
                            clipPath: `polygon(50% 50%, 100% 0, 100% 100%)`
                        }}
                    />
                ))}

                {/* Добавление вращающегося треугольника */}
                <div className={classes.triangle}></div>
            </div>
            <Gradus />

            {/* Кнопки управления */}
            <div style={{ position: 'absolute', bottom: '-50px', left: '50%', transform: 'translateX(-50%)', display: 'flex', gap: '10px' }}>
                {[3, 4, 5, 6].map(sectorId => (
                    <button
                        key={sectorId}
                        onClick={() => handleButtonClick(sectorId)}
                        style={{
                            padding: '5px 10px',
                            background: 'rgba(0, 255, 0, 0.2)',
                            border: '1px solid rgba(0, 255, 0, 0.5)',
                            color: '#00ff00',
                            cursor: 'pointer',
                            borderRadius: '4px'
                        }}
                    >
                        Сектор {sectorId}
                    </button>
                ))}
            </div>

            {/* Тут должна отображаться картинка при нажатии на кнопку */}
            {showImage && (
                <img
                    src={imageSrc}
                    alt="Пример"
                    className={classes.imgDron}
                />
            )}

            <div className={classes.degreeMarkings}>
                {Array.from({ length: 36 }).map((_, index) => (
                    <div
                        key={index}
                        className={`${classes.degreeMarking} ${classes[`rotate-${index * 10}`]}`}
                    ></div>
                ))}
            </div>
        </div>
    );
};

export default Radar;
