import React, { useState } from 'react';
import './App.css';

import Radar from './components/Radar/Radar';
import RulerRightLeft from './components/Ruler/RulerRightLeft';
import RulerTopBottom from './components/Ruler/RulerTopBottom';
import box from './Photo/eebc4b7dc05611ef8ab15ec00c958ca6_1.jpg';

let way = ''

function App() {
  const [showImage, setShowImage] = useState(false);

  const handleButtonClick = () => {
    setShowImage(true);
  };

  return (
    <div className="App">
      <nav>
        <main>
          <div className="square">
            <RulerTopBottom />
            <Radar showImage={showImage} imageSrc={box} />
            <RulerRightLeft />
          </div>
        </main>
        <aside>

          <button
            onClick={handleButtonClick}
          >
            Показать картинку
          </button>

          <div className="info-box">
            <h2>Информация</h2>
            <p>{way}</p>
          </div>
        </aside>
      </nav>
    </div>
  );
}

export default App;
