import classes from './Radar.module.css';

const Radar = () => {
    return (
        <div className={classes.radarContainer}>
            <div className={classes.radar}>
            <div className={classes.degreeLabel} id="top">360</div>
            <div className={classes.degreeLabel} id="right">90</div>
            <div className={classes.degreeLabel} id="bottom">180</div>
            <div className={classes.degreeLabel} id="left">270</div>
            </div>

            <div className={classes.degreeMarkings}>
                {Array.from({ length: 36 }, (_, i) => (
                    <div
                        key={i}
                        className={`${classes.degreeMarking} rotate-${i * 10}`}
                        style={{ transform: `rotate(${i * 10}deg)` }} // Добавляем поворот
                    ></div>
                ))}
            </div>
        </div>
    );
}

export default Radar;
